const mentions = $input.all();
const now = new Date();
const oneHourAgo = new Date(now.getTime() - (60 * 60 * 1000));

// Filter mentions from the last hour
const recentMentions = mentions.filter(item => {
  const tweetDate = new Date(item.json.created_at);
  return tweetDate >= oneHourAgo;
});

// Categorize mentions by sentiment (basic keyword analysis)
const categorizeMention = (text) => {
  const positiveWords = ['great', 'awesome', 'love', 'excellent', 'amazing', 'good', 'thanks', 'thank you'];
  const negativeWords = ['bad', 'terrible', 'hate', 'awful', 'problem', 'issue', 'complaint', 'disappointed'];

  const lowerText = text.toLowerCase();

  const positiveCount = positiveWords.filter(word => lowerText.includes(word)).length;
  const negativeCount = negativeWords.filter(word => lowerText.includes(word)).length;

  if (positiveCount > negativeCount) return 'positive';
  if (negativeCount > positiveCount) return 'negative';
  return 'neutral';
};

// Process mentions
const processedMentions = recentMentions.map(item => {
  const tweet = item.json;
  return {
    id: tweet.id,
    text: tweet.text,
    author: tweet.author_id,
    username: tweet.username || 'Unknown',
    created_at: tweet.created_at,
    sentiment: categorizeMention(tweet.text),
    metrics: tweet.public_metrics || {},
    url: `https://twitter.com/i/web/status/${tweet.id}`
  };
});

// Group by sentiment
const sentimentGroups = {
  positive: processedMentions.filter(m => m.sentiment === 'positive'),
  negative: processedMentions.filter(m => m.sentiment === 'negative'),
  neutral: processedMentions.filter(m => m.sentiment === 'neutral')
};

// Calculate engagement metrics
const totalEngagement = processedMentions.reduce((sum, mention) => {
  return sum + (mention.metrics.like_count || 0) + 
         (mention.metrics.retweet_count || 0) + 
         (mention.metrics.reply_count || 0);
}, 0);

return [{
  json: {
    summary: {
      total: processedMentions.length,
      positive: sentimentGroups.positive.length,
      negative: sentimentGroups.negative.length,
      neutral: sentimentGroups.neutral.length,
      totalEngagement: totalEngagement,
      timeRange: 'Last Hour',
      checkedAt: now.toISOString()
    },
    mentions: processedMentions,
    sentimentGroups: sentimentGroups,
    hasNewMentions: processedMentions.length > 0,
    highPriorityMentions: processedMentions.filter(m => 
      m.sentiment === 'negative' || 
      (m.metrics.retweet_count || 0) > 10 ||
      (m.metrics.like_count || 0) > 50
    )
  }
}];

