const invoices = $input.all();
const today = new Date();
today.setHours(0, 0, 0, 0);

const daysDifference = (date1, date2) => {
  const diffTime = date1 - date2;
  return Math.ceil(diffTime / (1000 * 60 * 60 * 24));
};

const formatCurrency = (amount) => {
  return new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD'
  }).format(amount);
};

const processedInvoices = invoices.map(item => {
  const invoice = item.json;

  const parseDate = (dateStr) => {
    if (!dateStr) return null;
    let date = new Date(dateStr);
    if (isNaN(date.getTime())) {
      const parts = dateStr.split('/');
      if (parts.length === 3) {
        date = new Date(parts[2], parts[0] - 1, parts[1]);
      }
    }
    return isNaN(date.getTime()) ? null : date;
  };

  const dueDate = parseDate(invoice.due_date || invoice.dueDate || invoice['Due Date']);
  const issueDate = parseDate(invoice.issue_date || invoice.issueDate || invoice['Issue Date']);

  if (!dueDate) {
    return null;
  }

  const daysUntilDue = daysDifference(dueDate, today);

  let reminderType = null;
  let priority = 'low';

  if (daysUntilDue <= 0) {
    reminderType = 'overdue';
    priority = 'high';
  } else if (daysUntilDue <= 3) {
    reminderType = 'urgent';
    priority = 'high';
  } else if (daysUntilDue <= 7) {
    reminderType = 'upcoming';
    priority = 'medium';
  } else if (daysUntilDue <= 14) {
    reminderType = 'early';
    priority = 'low';
  }

  return {
    invoiceNumber: invoice.invoice_number || invoice.invoiceNumber || invoice['Invoice Number'],
    clientName: invoice.client_name || invoice.clientName || invoice['Client Name'],
    clientEmail: invoice.client_email || invoice.clientEmail || invoice['Client Email'],
    amount: parseFloat(invoice.amount || invoice.total || invoice['Amount'] || 0),
    currency: invoice.currency || 'USD',
    dueDate: dueDate.toISOString().split('T')[0],
    issueDate: issueDate ? issueDate.toISOString().split('T')[0] : null,
    daysUntilDue: daysUntilDue,
    reminderType: reminderType,
    priority: priority,
    status: invoice.status || invoice['Status'] || 'pending',
    description: invoice.description || invoice['Description'] || 'Professional services'
  };
}).filter(invoice => invoice !== null && invoice.status !== 'paid' && invoice.reminderType);

const reminderGroups = {
  overdue: processedInvoices.filter(inv => inv.reminderType === 'overdue'),
  urgent: processedInvoices.filter(inv => inv.reminderType === 'urgent'),
  upcoming: processedInvoices.filter(inv => inv.reminderType === 'upcoming'),
  early: processedInvoices.filter(inv => inv.reminderType === 'early')
};

const totals = {
  overdue: reminderGroups.overdue.reduce((sum, inv) => sum + inv.amount, 0),
  urgent: reminderGroups.urgent.reduce((sum, inv) => sum + inv.amount, 0),
  upcoming: reminderGroups.upcoming.reduce((sum, inv) => sum + inv.amount, 0),
  all: processedInvoices.reduce((sum, inv) => sum + inv.amount, 0)
};

return [{
  json: {
    summary: {
      totalInvoices: processedInvoices.length,
      overdueCount: reminderGroups.overdue.length,
      urgentCount: reminderGroups.urgent.length,
      upcomingCount: reminderGroups.upcoming.length,
      earlyCount: reminderGroups.early.length,
      totals: totals,
      checkedAt: new Date().toISOString()
    },
    invoices: processedInvoices,
    reminderGroups: reminderGroups,
    needsReminders: processedInvoices.length > 0
  }
}];
